import React from 'react'
import Task from "./Task.js"
class Tasks extends React.Component {

    taskify = () => {
        return this.props.tasks.map(task=>{
            return <Task task={task} onClick={props.deleteTask}/>
        })
    }
    render(){
        return(
            <div className="tasks">
              <h5>Tasks</h5>
            {this.taskify()}
            </div>
        )
    }
}

export default Tasks
