import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import Categories from './Categories.js'
import Tasks from './Tasks.js'

class App extends React.Component {

  state = {
    catFilter: "",
    select: "All",
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  filteredTasks = () => {
    return this.state.tasks.filter(task => {
      return task.category.includes(this.state.catFilter)
    })
  }
  selectCategory = (e)=>{
    let value = e.target.textContent


    if (e.target.textContent === "All"){
      value = ""
    }
    console.log(value)
    this.setState({catFilter: value, selected: value})
  }
  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <Categories categories={CATEGORIES} selectCategory={this.selectCategory}/>
        <Tasks tasks={this.filteredTasks()} />
      </div>
    );
  }
}


export default App;
